(function() {
	var h = function(c, d) {
		d = d.replace(/\s/g, "").toLowerCase().split("");
		for(var a = c.toString().toLowerCase().split(""), b = 0, g = [], f = 0, e = 0; e < a.length; e++)if(a[e] === d[b] && (g.push(e), b++, f++, 0 === e || " " === a[e - 1] ? f++ : a[e - 1] === d[b - 1] && (f += 3)), b === d.length)return {priority: f, matches: g, text: c};
		return !1
	}, k = function(c, d) {
		for(var a = [], b = 0; b < c.length; b++)a.push(h(c[b], d));
		a = a.filter(function(a) {return !1 !== a});
		return a = a.sort(function(a, b) {return b.priority - a.priority})
	};
	Array.prototype.fuzzyMatches = function(c) {
		return k(this,
		         c)
	}
})();
